/**
 * 應用程式標題列元件
 */

import React from 'react';
import { Heart, MapPin, History } from 'lucide-react';
import { useLocation } from '../../stores/useAppStore';

interface HeaderProps {
  onLocationClick?: () => void;
  onHistoryClick?: () => void;
}

const Header: React.FC<HeaderProps> = ({ onLocationClick, onHistoryClick }) => {
  const { userLocation, locationPermission } = useLocation();

  return (
    <header className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-50">
      <div className="max-w-md mx-auto px-4 py-3">
        <div className="flex items-center justify-between">
          {/* Logo 和標題 */}
          <div className="flex items-center space-x-3">
            <div className="flex items-center justify-center w-10 h-10 bg-red-500 rounded-full">
              <Heart className="w-6 h-6 text-white" />
            </div>
            <div>
              <h1 className="text-lg font-bold text-gray-900">
                台灣醫療AI助手
              </h1>
              <p className="text-xs text-gray-500">
                症狀評估 • 就近就醫
              </p>
            </div>
          </div>

          {/* 功能按鈕 */}
          <div className="flex items-center space-x-2">
            {/* 位置按鈕 */}
            <button
              onClick={onLocationClick}
              className={`
                p-2 rounded-full transition-colors duration-200
                ${locationPermission && userLocation
                  ? 'bg-green-100 text-green-600 hover:bg-green-200'
                  : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                }
              `}
              title={
                locationPermission && userLocation
                  ? '已定位'
                  : '點擊取得位置'
              }
            >
              <MapPin className="w-5 h-5" />
            </button>

            {/* 歷史記錄按鈕 */}
            <button
              onClick={onHistoryClick}
              className="p-2 rounded-full bg-gray-100 text-gray-600 hover:bg-gray-200 transition-colors duration-200"
              title="查看歷史記錄"
            >
              <History className="w-5 h-5" />
            </button>
          </div>
        </div>

        {/* 位置資訊 */}
        {userLocation && (
          <div className="mt-2 text-xs text-gray-500 flex items-center">
            <MapPin className="w-3 h-3 mr-1" />
            <span>
              定位：{userLocation.latitude.toFixed(4)}, {userLocation.longitude.toFixed(4)}
            </span>
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
